import { Locale } from './locale.definitions';
/**
 * Represents widget base configuration
 * @public
 */
export interface IWidgetBaseConfig {
    /**
     * Locale language
     */
    locale?: Locale;
    /**
     * Controls widget style
     */
    style?: IWidgetStyle;
}
/**
 * Controls widget styling
 */
export interface IWidgetStyle {
    /**
     * Controls widget theme by Video Indexer
     */
    theme?: Theme;
    /**
     * Custom styling configuration
     */
    customStyle?: IBaseStyleConfig;
}
export type Theme = 'Default' | 'Dark';
/**
 * Base styling configuration
 */
export interface IBaseStyleConfig {
    highlight?: string;
    primary?: string;
    primaryHover?: string;
    primaryPress?: string;
    primaryDisable?: string;
    secondaryFill?: string;
    secondaryFillHover?: string;
    secondaryFillPress?: string;
    secondaryStroke?: string;
    componentFill?: string;
    componentStroke?: string;
    componentStrokeAlt?: string;
    playStatus?: string;
    playStatusAlt?: string;
    headerActions?: string;
    headerHovers?: string;
    headerHoversAlt?: string;
    headerBg?: string;
    dividers?: string;
    dividersAlt?: string;
    dividersPanel?: string;
    bgPrimary?: string;
    bgSecondary?: string;
    typePrimary?: string;
    typeSecondary?: string;
    typeDisabled?: string;
    typeDisabledAlt?: string;
    videoMenuBg?: string;
    videoShade?: string;
    errorType?: string;
    errorArea?: string;
    errorAreaTint?: string;
    warningArea?: string;
    shadow?: string;
}
